let fruits = [ "apple", "banana", "mango", "orange", "passionfruit", "grape" ];

let query = fruits.asEnumerable().takeWhile(fruit => "orange" !== fruit);

for (let fruit of query) {
    console.log(fruit);
}

/*
 This code produces the following output:

 apple
 banana
 mango
*/

2.js:
let fruits = [ "apple", "passionfruit", "banana", "mango", "orange", "blueberry", "grape", "strawberry" ];

let query = fruits.asEnumerable().takeWhile((fruit, index) => fruit.length >= index);

for (let fruit of query) {
    console.log(fruit);
}

/*
 This code produces the following output:

 apple
 passionfruit
 banana
 mango
 orange
 blueberry
*/
