let fruits = [ "apple", "passionfruit", "banana", "mango", "orange", "blueberry", "grape", "strawberry" ];

let query = fruits.asEnumerable().where(fruit => fruit.length < 6);

for (let fruit of query) {
    console.log(fruit);
}
/*
 This code produces the following output:

 apple
 mango
 grape
*/

2.js:
let numbers = [ 0, 30, 20, 15, 90, 85, 40, 75 ];

let query = numbers.asEnumerable().where((number, index) => number <= index * 10);

for (let number of query) {
    console.log(number);
}

/*
 This code produces the following output:

 0
 20
 15
 40
*/
