2.js:
let fruits2 = [ "橘子", "苹果" ];
let fruit2 = null;

try {
    fruit2 = fruits2.asEnumerable().single();
    console.log(fruit2);
} catch (e) {
    console.log("集合中不包含恰好一个元素。");
}

/*
 这段代码的输出结果如下：

 集合中不包含恰好一个元素。
*/

3.js:
let fruits = [ "苹果", "香蕉", "芒果", "橘子", "百香果", "葡萄" ];

let fruit1 = fruits.asEnumerable().single(fruit => fruit.length > 2);

console.log(fruit1);

/*
 这段代码的输出结果如下：

 百香果
*/

4.js:
let fruits = [ "苹果", "香蕉", "芒果", "橘子", "百香果", "葡萄" ];
let fruit2 = null;

try {
    fruit2 = fruits.asEnumerable().single(fruit => fruit.length > 5);

    console.log(fruit2);
} catch (e) {
    console.log("集合中不包含恰好一个长度超过5的元素。");
}

