1.js:
let fruits = [ "苹果", "百香果", "海南香蕉", "芒果", "橘子", "蓝莓", "新疆葡萄", "草莓" ];

let query = fruits.asEnumerable().where(fruit => fruit.length < 3);

for (let fruit of query) {
    console.log(fruit);
}
/*
 这段代码的输出结果如下：

 苹果
 芒果
 橘子
 蓝莓
 草莓
*/

2.js:
let numbers = [ 0, 30, 20, 15, 90, 85, 40, 75 ];

let query = numbers.asEnumerable().where((number, index) => number <= index * 10);

for (let number of query) {
    console.log(number);
}

/*
 这段代码的输出结果如下：

 0
 20
 15
 40
*/
